import React from 'react';
import { Chat, Channel, Attachment } from 'stream-chat-react';
import { ChannelHeader, MessageList, Window } from 'stream-chat-react';
import { MessageInput, Thread } from 'stream-chat-react';
import { StreamChat } from 'stream-chat';

import 'stream-chat-react/dist/css/index.css';

const chatClient = StreamChat.getInstance('kmtm6cx4ezxd');
const userToken = 'eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9.eyJ1c2VyX2lkIjoiZnJvc3R5LWJyZWV6ZS04In0.7RW0V8OcOB0dSg2yDPQMo8rHbzbVr7wbkC9msrX6kus';

class MyAttachmentComponent extends React.Component {
  render() {
    const { attachment } = this.props;
    if (attachment.type === 'product') {
      return (
        <div className='product'>
          Product:
          <a href={attachment.url} target='_blank'>
            <img src={attachment.image} height={'100px'} />
            <br />
            {attachment.name}
          </a>
        </div>
      );
    } else {
      return (
        <div>
          testing
          <Attachment {...this.props} />
        </div>
      );
    }
  }
}

chatClient.connectUser(
  {
    id: 'frosty-breeze-8',
    name: 'frosty-breeze-8',
    image: 'https://getstream.io/random_png/?id=frosty-breeze-8&name=frosty-breeze-8',
  },
  userToken,
);

const channel = chatClient.channel('messaging', 'godevs', {
  // add as many custom fields as you like
  image: 'https://cdn.chrisshort.net/testing-certificate-chains-in-go/GOPHER_MIC_DROP.png',
  name: 'Talk about Go',
});

const attachments = [
  {
    type: 'product',
    name: 'iPhone',
    url: 'https://goo.gl/ppFmcR',
    image: 'https://goo.gl/J2gQpi',
  },
];

channel.sendMessage({
  text: 'Your selected product is out of stock, would you like to select one of these alternatives?',
  attachments: attachments,
});

const App = () => (
  <Chat client={chatClient} theme='messaging'>
    <Channel channel={channel} Attachment={MyAttachmentComponent}>
      <Window>
        <ChannelHeader />
        <MessageList />
        <MessageInput />
      </Window>
      <Thread />
    </Channel>
  </Chat>
);

export default App;
